DROP TABLE "GBI_015"."STATIONS";

CREATE COLUMN TABLE "GBI_015"."STATIONS" (
"ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"LONGITUDE" VARCHAR (255),
"LATITUDE" VARCHAR (255),
"NAME" VARCHAR (255));

DROP TABLE "GBI_015"."SENSORS";

CREATE COLUMN TABLE "GBI_015"."SENSORS" (
"ID" INTEGER CS_INT NOT NULL GENERATED BY DEFAULT AS IDENTITY,
"NAME" VARCHAR (255));

DROP TABLE "GBI_015"."SENSOR_DATA";

CREATE COLUMN TABLE "GBI_015"."SENSOR_DATA" ("ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"BIKE" INTEGER,
"SENSOR" INTEGER,
"TIMESTAMP" TIMESTAMP,
"VALUE" DOUBLE);

DROP TABLE "GBI_015"."REPAIR_LOG";

CREATE COLUMN TABLE "GBI_015"."REPAIR_LOG" ("ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"BIKE" INTEGER,
"MECHANIC" INTEGER,
"SENSOR" INTEGER,
"REPAIR_TIME" TIMESTAMP);

DROP TABLE "GBI_015"."MECHANICS";

CREATE COLUMN TABLE "GBI_015"."MECHANICS" ("ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"FIRST_NAME" VARCHAR (255),
"LAST_NAME" VARCHAR (255));

DROP TABLE "GBI_015"."LENDING_LOG";

CREATE COLUMN TABLE "GBI_015"."LENDING_LOG" ("ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"BIKE" INTEGER,
"CUSTOMER" INTEGER,
"END_DATE" TIMESTAMP,
"END_STATION" INTEGER,
"START_DATE" TIMESTAMP,
"START_STATION" INTEGER);

DROP TABLE "GBI_015"."CUSTOMERS";

CREATE COLUMN TABLE "GBI_015"."CUSTOMERS" (
"ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"FIRST_NAME" VARCHAR (255),
"LAST_NAME" VARCHAR (255));

DROP TABLE "GBI_015"."BIKES";

CREATE COLUMN TABLE "GBI_015"."BIKES" ("ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"BIKE_TYPE" INTEGER);

DROP TABLE "GBI_015"."BIKE_TYPES";

CREATE COLUMN TABLE "GBI_015"."BIKE_TYPES" ("ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"INITIAL_AIR_PRESSURE" DOUBLE,
"MINIMUM_AIR_PRESSURE" DOUBLE,
"NAME" VARCHAR (255),
"WEARING_KILOMETER" DOUBLE);



