DROP TABLE "STATIONS";

CREATE COLUMN TABLE "STATIONS" (
"ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"LONGITUDE" VARCHAR (255),
"LATITUDE" VARCHAR (255),
"NAME" VARCHAR (255));

DROP TABLE "SENSORS";

CREATE COLUMN TABLE "SENSORS" (
"ID" INTEGER CS_INT NOT NULL GENERATED BY DEFAULT AS IDENTITY,
"NAME" VARCHAR (255));

DROP TABLE "SENSOR_DATA";

CREATE COLUMN TABLE "SENSOR_DATA" ("ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"BIKE" INTEGER,
"SENSOR" INTEGER,
"TIMESTAMP" TIMESTAMP,
"VALUE" DOUBLE);

DROP TABLE "REPAIR_LOG";

CREATE COLUMN TABLE "REPAIR_LOG" ("ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"BIKE" INTEGER,
"MECHANIC" INTEGER,
"SENSOR" INTEGER,
"REPAIR_TIME" TIMESTAMP);

DROP TABLE "MECHANICS";

CREATE COLUMN TABLE "MECHANICS" ("ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"FIRST_NAME" VARCHAR (255),
"LAST_NAME" VARCHAR (255));

DROP TABLE "LENDING_LOG";

CREATE COLUMN TABLE "LENDING_LOG" ("ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"BIKE" INTEGER,
"CUSTOMER" INTEGER,
"END_DATE" TIMESTAMP,
"END_STATION" INTEGER,
"START_DATE" TIMESTAMP,
"START_STATION" INTEGER);

DROP TABLE "CUSTOMERS";

CREATE COLUMN TABLE "CUSTOMERS" (
"ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"FIRST_NAME" VARCHAR (255),
"LAST_NAME" VARCHAR (255));

DROP TABLE "BIKES";

CREATE COLUMN TABLE "BIKES" ("ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"BIKE_TYPE" INTEGER);

DROP TABLE "BIKE_TYPES";

CREATE COLUMN TABLE "BIKE_TYPES" ("ID" INTEGER NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
"INITIAL_AIR_PRESSURE" DOUBLE,
"MINIMUM_AIR_PRESSURE" DOUBLE,
"NAME" VARCHAR (255),
"WEARING_KILOMETER" DOUBLE);



